package lc649;

import java.util.ArrayList;
import java.util.List;

/**
 * Leetcode question 649: Dota2 Senate
 * <a href="https://leetcode.com/problems/dota2-senate/">...</a>
 */
public class Run {
    public static void main(String[] args) {
        Run r = new Run("DRRD");
        Run r2 = new Run("");
    }

    public Run(String str) {
        System.out.println(predictPartyVictory(str));
    }

    public String predictPartyVictory(String senate) {
        List<Character> chars = new ArrayList<>(senate.chars().mapToObj(c -> (char) c).toList());
        int r = 0, d = 0, rCount = 0, dCount = 0;
        int index = 0;
        while (!chars.isEmpty()) {
            if (index > chars.size() - 1) {
                if (r > 0 && d > 0) {
                    r = 0;
                    d = 0;
                    index = 0;
                } else {
                    break;
                }
            }

            if (chars.get(index) == '-') {
            } else if (chars.get(index) == 'R') {
                if (dCount > 0) {
                    dCount--;
                    chars.set(index, '-');
                } else {
                    r++;
                    rCount++;
                }
            } else { // 'D'
                if (rCount > 0) {
                    rCount--;
                    chars.set(index, '-');
                } else {
                    d++;
                    dCount++;
                }
            }
            index++;
        }
        return r > 0 ? "Radiant" : "Dire";
    }
}
